import * as React from 'react';
import axios from 'axios';

type Props = {
  path: string;
  height: string | number;
  width: string | number;
}

type State = {
  html: string;
}

export default class Footer extends React.Component<Props, State> {

  constructor(props: Props) {
    super(props);
    this.state = { html: "" };
    axios
      .get(this.props.path)
      .then(res => {
        this.setState({
          html: res.data
        });
      });
  }

  markup() {
    return { __html: this.state.html };
  }

  render() {
    return (
      <div contentEditable key={Date()} dangerouslySetInnerHTML={this.markup()} />
    );
  }

}
