import * as React from 'react';
import ReactTable from 'react-table';
import axios from 'axios';
import '../index.css';
import 'react-table/react-table.css'

const ONE_HOUR: number = 3600000;

var tableStyle: React.CSSProperties = {
    width: "100%"
};

type State = {
    assegni: [];
    message: string;
}

type AssegniType = {
    data: Date;
    ok: number;
    errori: number;
    totale: number;
}

export default class Assegni extends React.Component<void, State> {

    constructor(props: any) {
        super(props);
        this.state = {
            assegni: [],
            message: ""
        }
    }

    getData() {
        axios
            .get('http://localhost:8090/assegni/all')
            .then(res => {
                this.setState({ assegni: res.data, message: "" });
            }).catch(error => {
                this.setState({ assegni: [], message: error.message });
            });
    }

    componentDidMount() {
        this.getData();
        setInterval(this.getData, ONE_HOUR);
    }

    render() {
        const columns = [{
            Header: 'Data',
            accessor: 'data',
            show: false
        }, {
            Header: 'Data',
            accessor: 'stringData'
        }, {
            Header: 'OK',
            accessor: 'ok'
        },
        {
            Header: 'Error',
            accessor: 'errori'
        },
        {
            Header: 'Total',
            accessor: 'totale'
        }];

        return (
            <div>
                <h3>Assegni</h3>
                <ReactTable
                    data={this.state.assegni}
                    columns={columns}
                    defaultPageSize={5}
                    showPagination={false}
                    defaultSorted={[
                        {
                            id: "data",
                            desc: true
                        }
                    ]}
                />
            </div>
        );
    }

}
